sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  var appJSON = inputs.appl;
  var appObj = JSON.parse(appJSON);
  var apps = appObj.result;

  for(i=0; i < apps.length; i++){
    var name = apps[i].name;
    var scope = apps[i].scope;
    var sysid = apps[i].sys_id;

    var gr = new GlideRecord('x_25421_safe_devop_remote_application');
    gr.get('application_sysid',sysid);
    gr.name = name;
    gr.scope = scope;
    gr.application_sysid = sysid;
    gr.update();
  }


})(inputs, outputs);

