sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//get assigned_to company
	var developer = current.assigned_to.getRefRecord();
	var company = developer.getValue('company');

	//query default task table
	var defaultTask = new GlideRecord('x_25421_safe_devop_safe_tasks');
	defaultTask.addEncodedQuery('active=true^companiesLIKE'+ company);
	defaultTask.query();
	while(defaultTask.next()){
		var condition = defaultTask.getValue('condition');
		var story = new GlideRecord('sn_safe_story');
		story.addEncodedQuery('sys_id='+current.sys_id+'^'+condition);
		story.query();
		if(story.next()){
			//does story task already exist?
			var storyTask = new GlideRecord('sn_safe_scrum_task');
			storyTask.addQuery('sn_safe_story',story.sys_id);
			storyTask.addQuery('x_25421_safe_devop_default_task',defaultTask.sys_id);
			storyTask.query();
			if(!storyTask.next()){
				//create story task since no previous task was already created
				gs.addInfoMessage('A new story task has been created: ' + defaultTask.short_description);
				storyTask.initialize();
				storyTask.sn_safe_story = story.sys_id;
				storyTask.priority = defaultTask.priority;
				storyTask.type = defaultTask.type;
				storyTask.assignment_group = story.assignment_group;
				storyTask.short_description = defaultTask.short_description;
				storyTask.description = defaultTask.description;
				storyTask.state = defaultTask.initial_state;
				storyTask.x_25421_safe_devop_default_task = defaultTask.sys_id;
				storyTask.insert();
			}
		}
	}

})(current, previous);
