sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	gs.addInfoMessage('Running Execute Flows');
	//query flow execution table
	var fe = new GlideRecord('x_25421_safe_devop_flow_execution');
	fe.query();
	while(fe.next()){
		var condition = fe.getValue('conditions');

		var story = new GlideRecord('sn_safe_story');
		story.addEncodedQuery('sys_id=' + current.sys_id + '^' + condition);
		story.query();
		if(story.next()){

			var flow = new x_25421_safe_devop.SADevOpsUtils();
			flow.getFlowRecord(fe.flow);

			var scope = new GlideRecord('sys_scope');
			scope.get(flow.sys_scope);
			var scopeName = scope.scope;

			var flowName = scopeName + '.' + flow.internal_name;
			gs.addInfoMessage('Flow executed ' + flowName);
			var inputs = {};
			inputs['story'] = story;
			flow.startSubflow(flowName,inputs);
		}
	}

})(current, previous);
