sys_script_include.script:
var SADevOpsUtils = Class.create();
SADevOpsUtils.prototype = {
    initialize: function() {
		this.result = "Parameter Missing";
		this.sys_scope;
		this.internal_name;
    },
	startFlow : function(i) {
		//accepts Flow internal name as parameter
		if(i){
			sn_fd.FlowAPI.executeFlow(i);
			this.result = "Success";
		}
	},
	startSubflow : function(i,inputs) {
		//accepts Flow internal name as parameter, inputs
		if(i){
			sn_fd.FlowAPI.executeSubflow(i,inputs);
			this.result = "Success";
		}
	},
	getFlowRecord : function(id){
		var fl = new GlideRecord('sys_hub_flow');
		fl.get(id);
		this.sys_scope = fl.sys_scope;
		this.internal_name = fl.internal_name;

	},

    type: 'SADevOpsUtils'
};
